sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    //申請者・管理者のみ、申請ステータスが起票中の場合に削除可
    //Adminは常に可
    var num = current.number;
    var srv_info = '';

    var gr_1 = new GlideRecord('x_761427_campusdev_01_server_request_base');
    gr_1.addQuery('u_01_request_form_number', num);
    gr_1.query();

    var gr_2 = new GlideRecord('x_761427_campusdev_02_server');
    gr_2.addQuery('u_02_request_form_number.u_01_request_form_number', num);
    gr_2.query();

    var gr_7 = new GlideRecord('x_761427_campusdev_07_affinity_rule');
    gr_7.addQuery('u_07_request_form_number.u_01_request_form_number', num);
    gr_7.query();

    //とれない↓	
    //srv_info = gr_2.getUniqueValue();
    //		gs.info('ろぐ２' + srv_info);


    while (gr_2.next()) {
        //getUniqueValueは引数不要
       // srv_info = gr_2.getUniqueValue();
 srv_info = gr_2.sys_id;

        var gr_3 = new GlideRecord('x_761427_campusdev_03_disk');
        gr_3.addQuery('u_03_server_info', srv_info);
        gr_3.query();

        var gr_4 = new GlideRecord('x_761427_campusdev_04_rdm_disk');
        gr_4.addQuery('u_04_server_info', srv_info);
        gr_4.query();

        var gr_5 = new GlideRecord('x_761427_campusdev_05_nic');
        gr_5.addQuery('u_05_server_info', srv_info);
        gr_5.query();

        var gr_6 = new GlideRecord('x_761427_campusdev_06_data_backup');
        gr_6.addQuery('u_06_server_info', srv_info);
        gr_6.query();

        while (gr_3.next()) {
			gs.info('gr_3ろぐ：' + gr_3.getUniqueValue());
            gr_3.deleteMultiple();
            gs.info('03削除');
        }
        while (gr_4.next()) {
            gr_4.deleteMultiple();
            gs.info('04削除');
        }
        while (gr_5.next()) {
            gr_5.deleteMultiple();
            gs.info('05削除');
        }
        while (gr_6.next()) {
            gr_6.deleteMultiple();
            gs.info('06削除');
        }
        gr_2.deleteMultiple();
    }
    while (gr_7.next()) {
        gr_7.deleteMultiple();
        gs.info('07削除');
    }
    if (gr_1.next()) {
        gr_1.deleteMultiple();

    }

})(current, previous);
