sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below

   if (g_scratchpad.isFormValid)//2回目実行時はそのまま終了
        return true;

	var osType         = g_form.getValue('u_02_os_type');
	var usage          = g_form.getValue('u_02_usage');
	var requestBaseRef = g_form.getReference('u_02_request_form_number', callback);
	return false;

	function callback(ref){
		var ga = new GlideAjax('camdev_ValidationServerRequestClient');
		ga.addParam('sysparm_name', 'isAutomation');
		ga.addParam('sysparm_classification', ref.u_01_classification);
		ga.addParam('sysparm_server_type', ref.u_01_server_type);
		ga.addParam('sysparm_ostype', osType);
		ga.addParam('sysparm_usage', usage);
		ga.getXMLAnswer(setAnswer);

		function setAnswer(answer) {
			var automaionFlag = 'false';
			if (answer == 'true'){
				automaionFlag = 'true';
			}
			g_form.setValue('u_02_automation_flag', automaionFlag);
			var actionName = g_form.getActionName();
			g_scratchpad.isFormValid = true;
			g_form.submit(actionName);//再度送信処理を行う
		}
	}
}
