sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    //一部の項目に必須入力設定をする
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_usage_system', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_environment_classification', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_network_cable_connection', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_new_network', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_classification', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_server_type', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_sub_system', true);
    g_form.setMandatory('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_manager', true);

    if (g_form.isNewRecord()) { //新規レコード作成時

		// サーバー情報を入力する為の操作説明メッセージを表示する。
        g_form.addInfoMessage(getMessage('camdev_08_InfoMessage001'));

        //「申請オーナー」フィールド、「申請ステータス」フィールドに初期値を設定する
        g_form.setValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_request_owner', g_user.userID);
        g_form.setValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_request_status', 10);

    } else if (!g_form.isNewRecord()) { //作成済みレコードオープン時
        //「申請ステータス」が「申請起票中」でかつログインユーザと「申請オーナー」が異なる場合、注意メッセージを表示する。
        var request_status = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_request_status');
        var RequestOwner = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_request_owner');
        if (request_status == 10 && RequestOwner != g_user.userID) {
            g_form.addErrorMessage(getMessage('camdev_08_RequestOwnerDifferentMessage'));
        }

        //サーバー情報に自動化対象と自動化対象外が混在している場合、警告メッセージを表示する。
        var ajax = new GlideAjax('camdev_AutomatedMixedCheck');
        ajax.addParam('sysparm_name', 'funcAutoFlgMixedCheck');
        ajax.addParam('sysparm_system_id', g_form.getValue('u_74_external_ref'));

        ajax.getXMLAnswer(function(answer) {
            if ('true' == answer) {
                g_form.addWarningMessage(getMessage('camdev_00_WarningMessage001'));
            }
        });
    }
}
