sys_script_client.script:
function onLoad() {

    /**
     * 区分「新規」かつサーバ種別「ベアメタル」かどうかを判定
     * 上記と一致する場合はtrue、それ以外をfalseとする
     * trueの場合はメッセージを表示する
     * ※開発規約の文字数制限により、呼び出し元のClient Script側でのNameを「Create」ではなく「New」としている。
     */

    //sys_idの取得
    var sysid = g_form.getValue('u_02_request_form_number');
    //テーブル名を定義
    var table_name = 'x_761427_campusdev_01_server_request_base';
    //Script Includeの呼び出し
    var ga = new GlideAjax('camdev_FormMessageNewBareMetal');
    ga.addParam('sysparm_name', 'judgement_func');
    ga.addParam('sysparm_sysid', sysid);
    ga.addParam('sysparm_tablename', table_name);

    ga.getXMLAnswer(function(answer) {

        if (answer == 'true') {
            var no_input_message = '区分「新規」かつサーバ種別「ベアメタル」の場合は入力不要です。';
            g_form.showFieldMsg('u_02_cpu_socket_count', no_input_message, 'info');
            g_form.showFieldMsg('u_02_virtual_nic_count', no_input_message, 'info');
            g_form.showFieldMsg('u_02_affinity_rule', no_input_message, 'info');
        }
    });
}
