sys_script_client.script:
function onLoad() {

    /* サーバ種別「IA-Gr」か判定し、trueの場合は項目下にメッセージを表示する  */
    var server_type = g_form.getValue('u_02_request_form_number.u_01_server_type');
    if (server_type == 'IA-Gr') {
        var no_input_message = 'サーバ種別「IA-Gr」の場合は入力不要です。';
        g_form.showFieldMsg('u_02_hyper_threading', no_input_message, 'info');
    }

    /* ロール種別「申請者ロールのみ」且つ、申請ステータス「申請起票中」の場合、メッセージを表示する  */
    if (g_user.hasRole('x_nttm8_campus_dev.individual_system_requester')) {
        //sys_idの取得
        var sysid = g_form.getValue('u_02_request_form_number');

        //Script Includeの呼び出し
        var ga = new GlideAjax('camdev_FormMessageJudgment');
        ga.addParam('sysparm_name', 'func_judge_requestStatus_parenttable');
        ga.addParam('sysparm_sysid', sysid);
        ga.addParam('sysparm_judgment_status', 10);

        ga.getXMLAnswer(function(answer) {
            if (answer == 'true') {
                g_form.addWarningMessage(getMessage('camdev_02_WarningMessage001'));
            }
        });
    }
}
