function onCellEdit(sysIDs, table, oldValues, newValue, callback) {

    var saveAndClose = true;

    var ajax = new GlideAjax('camdev_AutoSetRwRateRead');
    ajax.addParam('sysparm_name', 'autoSetRwRateRead');
    ajax.addParam('sysparm_table_name', table); //parameter:table name
    ajax.addParam('sysparm_system_ids', sysIDs); //parameter:sys_ids
    ajax.addParam('sysparm_read_num', newValue); //parameter:RW Rate Read

    ajax.getXMLAnswer(function(answer) {});

    callback(saveAndClose);

    GlideList2.get(g_form.getTableName() + '.x_761427_campusdev_04_rdm_disk.u_04_server_info').setFilterAndRefresh('');
}
