    ga.addParam('sysparm_system_ids', sysIDs);
    ga.addParam('sysparm_target_feild', 'u_02_usage');
    ga.addParam('sysparm_new_value', newValue);

    ga.getXMLAnswer(function(answer) {});

    GlideList2.get(g_form.getTableName() + '.REL:5e911f991b12855093ae98e9bc4bcbc3').setFilterAndRefresh('');

    callback(saveAndClose);
}
