sys_script_client.script:
function onLoad() {

    //02テーブルのsys_idの取得
    var sysid = g_form.getValue('u_05_server_info');
	//Script Includeの呼び出し
    var ga = new GlideAjax('camdev_FormMessageJudgment');
    ga.addParam('sysparm_name', 'func_judge_requestStatus_grandparenttable');
    ga.addParam('sysparm_sysid', sysid);
    ga.addParam('sysparm_tablename', 'x_761427_campusdev_02_server');	
    ga.addParam('sysparm_judgment_status', 10);

    ga.getXMLAnswer(function(answer) {
        if (answer == 'true') {
            g_form.addWarningMessage(getMessage('camdev_05_WarningMessage001'));
        }
    });
}
