(function executeRule(current, previous /*null when async*/ ) {

    var currentName = current.getValue('name');
    var currentQueue = current.getValue('queue');
    var textStart = '<stdout>';
    var textEnd = '</stdout>';
    var lengthPayload = '';
    var positionStart = '';
    var positionEnd = '';
    var payloadSlice = '';
    var filePathEncode = 'base64 /home/ec2-user/snmid/agent/tst01.csv';
