    var tableName = 'sys_data_source';
    var tableRecordSysId = '8d72ad722fa09550ea364ae72799b63a';

    var stringUtil = GlideStringUtil;
    var attachment = new camdevAttachment();


    if (currentName == filePathEncode && currentQueue == 'input') {
        lengthPayload = (currentPayload.match(new RegExp(textStart, "g")) || []).length;

        if (0 != lengthPayload) {

            if (lengthPayload == 1) {

                positionStart = currentPayload.indexOf(textStart, 0);
                positionEnd = currentPayload.indexOf(textEnd, 0);
                payloadSlice = currentPayload.slice(positionStart + 8, positionEnd);
             //   gs.info(payloadSlice + 'ペイロード');
                //var stringUtil = GlideStringUtil;
                //  var stringUtil = new GlideStringUtil();
                //               var attachment = new camdevAttachment();
                var base64Decode = stringUtil.base64DecodeAsBytes(payloadSlice);
