                attachment.write('sys_data_source', '8d72ad722fa09550ea364ae72799b63a', 'aaa.csv', 'text/csv', base64Decode);

            } else {

                for (var ii = 0; ii < lengthPayload; ii++) {

                    positionStart = currentPayload.indexOf(textStart, ii);
                    positionEnd = currentPayload.indexOf(textEnd, ii);
                    payloadSlice = currentPayload.slice(positionStart + 8, positionEnd);

                    //attachment.write(tableName, tableRecordSysId, 'aaa.csv', 'text/csv', stringUtil.base64DecodeAsBytes(payloadSlice));				
                }
            }
        }
    }
})(current, previous);
