sys_script_include.script:
var camdev_AutomatedMixedCheck = Class.create();
camdev_AutomatedMixedCheck.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
 funcAutoFlgMixedCheck: function() {
        var input_sysid = this.getParameter('sysparm_system_id');

        var gr = new GlideRecord("x_761427_campusdev_02_server");
        gr.addQuery("u_02_request_form_number", input_sysid);
        gr.query();

        var automation_flg_off = false;
        var automation_flg_on = false;
        while (gr.next()) {

             if ('false' == gr.u_02_automation_flag) {
                automation_flg_off = true;
            } else {
                automation_flg_on = true;
            }
            if (automation_flg_off && automation_flg_on) {
                return true;
            }
        }
        return false;
    },

    type: 'camdev_AutomatedMixedCheck'
});
