sys_script_include.script:
var camdev_FormMessageNewBareMetal = Class.create();
camdev_FormMessageNewBareMetal.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /**
     * 区分「新規」かつサーバ種別「ベアメタル」を判定
     * 上記に一致する場合はtrue、それ以外はfalseを返却
     * ※02及び07テーブルは下記「judgement_func」、03～05テーブルは「judgement_func_other」を使用。
     * ※開発規約の文字数制限により、呼び出し元のClient Script側でのNameを「Create」ではなく「New」としている。
     * その為、本Script Includeも上記に準じた命名としている。
     */

    judgement_func: function() {
        var sysid = this.getParameter('sysparm_sysid');
        var tablename = this.getParameter('sysparm_tablename');

        var gr = new GlideRecord(tablename);
        gr.addQuery('sys_id', '=', sysid);
        gr.query();

        while (gr.next()) {
            var classification_value = gr.u_01_classification;
            var server_type_value = gr.u_01_server_type;
            if (classification_value == 'create' && server_type_value == 'bare-metal') {
                return "true";
            } else {
                return "false";
            }
        }
    },

    judgement_func_other: function() {

        var sysid = this.getParameter('sysparm_sysid');
        var tablename = this.getParameter('sysparm_tablename');
        var gr = new GlideRecord(tablename);
        gr.addQuery('sys_id', '=', sysid);
        gr.query();

        while (gr.next()) {
            var dotwalk_sysid = gr.u_02_request_form_number;
            var classification_value = dotwalk_sysid.u_01_classification;
            var server_type_value = dotwalk_sysid.u_01_server_type;

            if (classification_value == 'create' && server_type_value == 'bare-metal') {
                return "true";
            } else {
                return "false";
            }
        }
    },	

    type: 'camdev_FormMessageNewBareMetal'
});
