sys_script_include.script:
var camdev_FormMessageJudgment = Class.create();
camdev_FormMessageJudgment.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

   /**
     * パラメータで指定されたjudgment_statusと01テーブルの申請ステータスが一致するかを判定
     * 上記に一致する場合はtrue、それ以外はfalseを返却
	 * ※02,07テーブルから判定する場合は当関数を使用すること	 
     * ※パラメータのSYS-ID は、referenceのSYS-ID（01テーブルのSYS-ID）であること
     */
    func_judge_requestStatus_parenttable: function() {
        var sysid = this.getParameter('sysparm_sysid');
        var judgment_status = this.getParameter('sysparm_judgment_status');

        var gr = new GlideRecord('x_761427_campusdev_01_server_request_base');
        gr.addQuery('sys_id', '=', sysid);
        gr.query();

        while (gr.next()) {
            var request_status = gr.u_01_request_status;
            if (judgment_status == request_status) {
                return "true";
            } else {
                return "false";
            }
        }
    },

    /**
     * パラメータで指定されたjudgment_statusと01テーブルの申請ステータスが一致するかを判定
     * 上記に一致する場合はtrue、それ以外はfalseを返却
	 * ※03~06テーブルから判定する場合は当関数を使用すること	 
     * ※パラメータのSYS-ID は、referenceのSYS-ID（02テーブルのSYS-ID）であること
     */
    func_judge_requestStatus_grandparenttable: function() {
        var sysid = this.getParameter('sysparm_sysid');
        var tablename = this.getParameter('sysparm_tablename');
        var judgment_status = this.getParameter('sysparm_judgment_status');

		var gr = new GlideRecord('x_761427_campusdev_02_server');
        gr.addQuery('sys_id', '=', sysid);
        gr.query();

        while (gr.next()) {
            var dotwalk_sysid = gr.u_02_request_form_number;
            var request_status = dotwalk_sysid.u_01_request_status;

            if (judgment_status == request_status) {
                return "true";
            } else {
                return "false";
            }
        }
    },	

    type: 'camdev_FormMessageJudgment'
});
