sys_script_include.script:
var camdev_AutoSetRwRateRead = Class.create();
camdev_AutoSetRwRateRead.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
   autoSetRwRateRead: function() {
        // パラメータ取得
        var target_tbl_name = this.getParameter("sysparm_table_name");
        var target_sysids = this.getParameter("sysparm_system_ids");
        var read_num = this.getParameter("sysparm_read_num");

		var target_Field = '';
		if( 'x_761427_campusdev_03_disk' == target_tbl_name){
			target_Field = 'u_03_rw_rate_write';
		}else if('x_761427_campusdev_04_rdm_disk' == target_tbl_name){
			target_Field = 'u_04_rw_rate_write';			
		}else{
			return;
		}

		var set_value = ''; 
		if(read_num != ''){
			set_value = 10 - parseInt(read_num, 10); 
		}

        var target_array = target_sysids.split(',');
        for (var i = 0; i < target_array.length; i++) {
            var gr = new GlideRecord(target_tbl_name);
            gr.get(target_array[i]);
            gr.setValue(target_Field, set_value);
            gr.update();
        }
    },
    type: 'camdev_AutoSetRwRateRead'
});
