sys_script_include.script:
function camdev_GetReferenceAnsibleNicNo() {

    /**
     * 05テーブルのレコードを検索し02テーブルとsys_idが一致するものを抽出。
     * 一致したものがあった場合、05レコードのsys_idに'sys_idIN'を付加して返却。
     * ※u_02_ansible_nic_noのAdvanced viewのReference Specification-Reference qualに
     * 「javascript:camdev_GetReferenceAnsibleNicNo()」と記載し、本Script Includeを使用。
     */

    var return_sys_id = '';
    var this_sys_id = current.getUniqueValue();

    var gr = new GlideRecord('x_761427_campusdev_05_nic');
    gr.addQuery('u_05_server_info', '=', this_sys_id);
    gr.query();

    while (gr.next()) {
        return_sys_id = return_sys_id + ',' + gr.sys_id;
    }
    return 'sys_idIN' + return_sys_id;

}
