sys_ui_action.script:
function provisioning_re_inputcomplete() {
	// 画面項目値の取得
	var svReqBaseSysId        = g_form.getValue('u_74_external_ref');
    var classification        = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_classification');
    var classificationDisplay = g_form.getOption('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_classification', classification).text;
    var serverType            = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_server_type');
    var serverTypeDisplay     = g_form.getOption('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_server_type', serverType).text;
    var nweNw                 = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_new_network');
    var nwCableConnection     = g_form.getValue('u_74_external_ref.ref_x_761427_campusdev_01_server_request_base.u_01_network_cable_connection');

    // チェック処理を実施し、エラーがない場合のみ更新
    var validationSrc = new GlideAjax('camdev_ValidationServerRequestClient');
    validationSrc.addParam('sysparm_name',                         'validationForProvisioningReInputComplete');
    validationSrc.addParam('sysparm_svreqbase_sysid',              svReqBaseSysId);
    validationSrc.addParam('sysparm_classification',               classification);
    validationSrc.addParam('sysparm_classification_display_value', classificationDisplay);
    validationSrc.addParam('sysparm_server_type',                  serverType);
    validationSrc.addParam('sysparm_new_nw',                       nweNw);
    validationSrc.addParam('sysparm_nw_cable_connection',          nwCableConnection);
    validationSrc.getXMLAnswer(function(answer) {
        var errorMsgs = JSON.parse(answer);
        if (errorMsgs != null && errorMsgs.length > 0) {
            g_form.clearMessages();
            g_form.addErrorMessage(errorMsgs.join('<br/>'));
			return false;
        } else {
			validationWarning(svReqBaseSysId, classification, classificationDisplay, serverType);
        }
    });

	// 警告チェック処理を実施
	function validationWarning(srbSysId, classification, classificationDisplay, serverType) {
		var validationSrc = new GlideAjax('camdev_ValidationServerRequestClient');
		validationSrc.addParam('sysparm_name',                         'validationForServerRequestWarning');
		validationSrc.addParam('sysparm_svreqbase_sysid',              srbSysId);
		validationSrc.addParam('sysparm_classification',               classification);
		validationSrc.addParam('sysparm_classification_display_value', classificationDisplay);
		validationSrc.addParam('sysparm_server_type',                  serverType);
		validationSrc.getXMLAnswer(function(answer) {
			var warningMsgs = JSON.parse(answer);
			if (warningMsgs != null && warningMsgs.length > 0) {
				var isWarning = confirm(warningMsgs.join('\n'));
				if(!isWarning) {
					return false;
				} else {
					//区分「新規」かつサーバ種別「ベアメタル」の場合、入力不要項目チェック
					if (classification == 'create' && serverType == 'bare-metal') {
						var clearItemMsg = 	'　CPUソケット数' + '\n'
										+ '　仮想NIC数' + '\n'
										+ '　配置制御設定アフィニティルール設定希望有無' + '\n'
										+ '　仮想ディスク詳細情報[IOPS]' + '\n'
										+ '　RDMディスク詳細情報[IOPS]' + '\n'
										+ '　NIC詳細情報[最大使用NW帯域(Mbps)]' + '\n'
										+ '　配置制御設定アフィニティルール' + '\n';

						var clearConfirmMsg = '区分が' + classificationDisplay + 'の' + serverTypeDisplay + 'の申請時、以下の項目は入力不要です。' + '\n'
											+ 'クリアして申請依頼を行いますがよろしいですか？' + '\n'
											+ clearItemMsg;
						var isUpdate = confirm(clearConfirmMsg);
						if (!isUpdate) {
							return false;
						} else {
							var noInputCheckSrc = new GlideAjax('camdev_NoInputCheckNewBareMetal');
							noInputCheckSrc.addParam('sysparm_name', 'noInputCheck');
							noInputCheckSrc.addParam('sysparm_sysid', srbSysId);
							noInputCheckSrc.getXMLAnswer(function(answer) {
								var messages = '以下の項目をクリアしました。\n' + clearItemMsg;
								alert(messages);
							});
						}
					}
				}
			}
			gsftSubmit(null, g_form.getFormElement(), "camdevinputcomplete_re_fromprovisioning");
		});
	}
}

if (typeof window == 'undefined')
   setRedirect();

function setRedirect() {
	current.update();
    action.setRedirectURL(current);
	var refRecord         = current.u_74_external_ref.getRefRecord();
	var refSysId          = current.u_74_external_ref;
	var refClassification = refRecord.getValue('u_01_classification');
	var refServerType     = refRecord.getValue('u_01_server_type');
	var postValidMsgs     = new camdev_ValidationServerRequest().postValidationForProvisioningInputComplete(refSysId, refClassification, refServerType);
	var message           = "入力内容の必須チェック・妥当性チェックが完了しました。解消しなければならないエラーはありません。";
	if (postValidMsgs != null && postValidMsgs.length > 0) {
		message = "以下を除き、入力内容の必須チェック・妥当性チェックが完了しました。必ず解消しなければならないエラーではありませんが、必要に応じて修正してください。<br/>"
					+ "<b><font style='background-color:#fff3cd;border-color:#ffeeba'>"
					+ postValidMsgs.join('<br/>')
					+ "</font></b>";
	}
	gs.addInfoMessage(message);
}
